mapper.update:
update attendance set CHECK_OUT = CURRENT_TIMESTAMP
, ATTENDANCE_STATUS = #{attendanceStatus}
where (MEMBER_ID = #{memberId}) and (to_char(ATTENDANCE_DATE, 'YYYY-MM-DD') = #{today})

UPDATE STATISTICS
SET


	ABSENT = (
			SELECT
				(S.ABSENT+1) AS ABSENT
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(A.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=to_number(#{month})
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}


	attendance = (
			SELECT 
				attendance+1 AS attendance
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(a.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=#{month}
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}


	late = (
			SELECT 
				late+1 AS late
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(a.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=#{month}
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}


	leave = (
			SELECT 
				leave+1 AS leave
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(a.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=#{month}
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}

mapper.select:

select to_char(CHECK_IN, 'HH24:mi:ss')       
from attendance
where ATTENDANCE_ID == #{attendanceId}

mapper.update:

UPDATE ATTENDANCE 
	SET SUBMIT_STATUS = #{submitStatus}
WHERE ATTENDANCE_ID=(select ATTENDANCE_ID  from cause where CAUSE_ID=#{causeId})

choose.when:
update attendance
set ATTENDANCE_STATUS =
#{attendanceStatus}
where ATTENDANCE_ID = #{attendanceId}

choose.otherwise:
update attendance
				set ATTENDANCE_STATUS =
				#{attendanceStatus}
				where MEMBER_ID = #{memberId} and
				to_char(ATTENDANCE_DATE, 'YYYY-MM-DD') = #{today}
